/*global tinymce:true */

tinymce.PluginManager.add('help', function(editor) {
	
	editor.addButton('help', {
		title: '使用说明',
		icon:'help',
		onclick: function() {
         editor.windowManager.open({
            title: '在线公式编辑器使用说明',
            width: '1000',
            height:'700',
            url: '../README.pdf'
           
         });
         
      }
	});
	
	
});
